// Complete Kanban board implementation
import React, { useState, useEffect, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { supabase } from '@/lib/supabase';
import { TaskCard } from './TaskCard';
import { ColumnHeader } from './ColumnHeader';

export default function KanbanBoard({ projectId }) {
  const [tasks, setTasks] = useState([]);
  const [columns, setColumns] = useState([
    { id: 'todo', title: 'To Do', color: 'gray' },
    { id: 'in-progress', title: 'In Progress', color: 'blue' },
    { id: 'review', title: 'Review', color: 'yellow' },
    { id: 'done', title: 'Done', color: 'green' }
  ]);

  // Fetch tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, [projectId]);

  const fetchTasks = useCallback(async () => {
    const { data, error } = await supabase
      .from('tasks')
      .select(`
        *,
        assignee:users!tasks_assignee_id_fkey(id, name, avatar),
        creator:users!tasks_creator_id_fkey(id, name, avatar)
      `)
      .eq('project_id', projectId)
      .order('created_at', { ascending: false });

    if (error) {
      console.error('Error fetching tasks:', error);
      return;
    }

    setTasks(data || []);
  }, [projectId]);

  // Real-time subscription for live updates
  useEffect(() => {
    const channel = supabase
      .channel(`tasks-${projectId}`)
      .on('postgres_changes', {
        event: '*',
        schema: 'public',
        table: 'tasks',
        filter: `project_id=eq.${projectId}`
      }, (payload) => {
        console.log('Real-time update:', payload);
        fetchTasks(); // Refresh tasks on any change
      })
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [projectId, fetchTasks]);

  // Handle drag end with optimistic updates
  const handleDragEnd = useCallback(async (result) => {
    const { source, destination, draggableId } = result;

    // Dropped outside a droppable area
    if (!destination) return;

    // No change in position
    if (source.droppableId === destination.droppableId && 
        source.index === destination.index) {
      return;
    }

    // Optimistic update for smooth UX
    const newTasks = Array.from(tasks);
    const movedTask = newTasks.find(task => task.id === draggableId);

    if (movedTask) {
      movedTask.status = destination.droppableId;
      movedTask.updated_at = new Date().toISOString();
      setTasks(newTasks);

      // Update in database
      try {
        const { error } = await supabase
          .from('tasks')
          .update({ 
            status: destination.droppableId,
            updated_at: new Date().toISOString()
          })
          .eq('id', draggableId);

        if (error) {
          console.error('Error updating task:', error);
          // Revert optimistic update on error
          fetchTasks();
        }
      } catch (error) {
        console.error('Network error:', error);
        fetchTasks(); // Revert on network error
      }
    }
  }, [tasks, fetchTasks]);

  // Group tasks by status
  const getTasksByStatus = useCallback((status) => {
    return tasks.filter(task => task.status === status);
  }, [tasks]);

  return (
    <div className="h-full flex flex-col">
      {/* Board Header */}
      <div className="flex items-center justify-between p-4 border-b">
        <h2 className="text-xl font-semibold">Task Board</h2>
        <div className="flex items-center space-x-4 text-sm text-gray-600">
          <span>{tasks.length} tasks</span>
          <span>{columns.length} columns</span>
        </div>
      </div>

      {/* Kanban Columns */}
      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="flex-1 flex overflow-x-auto p-4 space-x-4">
          {columns.map((column) => {
            const columnTasks = getTasksByStatus(column.id);
            return (
              <div key={column.id} className="flex-shrink-0 w-80">
                <ColumnHeader 
                  column={column} 
                  taskCount={columnTasks.length} 
                />
                <Droppable droppableId={column.id}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className={`min-h-[400px] p-2 rounded-lg transition-colors ${
                        snapshot.isDraggingOver 
                          ? 'bg-blue-50 border-2 border-blue-300 border-dashed' 
                          : 'bg-gray-50'
                      }`}
                    >
                      {columnTasks.map((task, index) => (
                        <Draggable 
                          key={task.id} 
                          draggableId={task.id} 
                          index={index}
                        >
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className={`mb-2 transform transition-transform ${
                                snapshot.isDragging 
                                  ? 'rotate-3 scale-105 shadow-lg' 
                                  : ''
                              }`}
                            >
                              <TaskCard task={task} />
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </div>
            );
          })}
        </div>
      </DragDropContext>
    </div>
  );
}

